-- Flash.nvim - Navigate your code with search labels and Treesitter integration
-- https://github.com/folke/flash.nvim

return {
  'folke/flash.nvim',
  event = 'VeryLazy',
  opts = {
    -- Configure flash.nvim behavior
    modes = {
      -- Customize search mode
      search = {
        enabled = true,
        highlight = { backdrop = false },
      },
      -- Customize character mode
      char = {
        enabled = true,
        jump_labels = true,
      },
    },
    -- Treesitter mode configuration
    treesitter = {
      labels = 'abcdefghijklmnopqrstuvwxyz',
      jump = { pos = 'range', autojump = true },
      search = { incremental = false },
      label = { before = true, after = true, style = 'inline' },
      highlight = {
        backdrop = false,
        matches = false,
      },
    },
    -- Label configuration
    label = {
      uppercase = false,
      rainbow = {
        enabled = false,
      },
    },
    -- Highlight configuration
    highlight = {
      backdrop = true,
      matches = false,
      priority = 5000,
      groups = {
        match = 'FlashMatch',
        current = 'FlashCurrent',
        backdrop = 'FlashBackdrop',
        label = 'FlashLabel',
      },
    },
    -- Action configuration
    action = nil,
    -- Pattern configuration
    pattern = '',
    -- Continue last search
    continue = false,
    -- Prompt configuration
    prompt = {
      enabled = true,
      prefix = { { '⚡', 'FlashPromptIcon' } },
    },
  },
  keys = {
    {
      's',
      mode = { 'n', 'x', 'o' },
      function()
        require('flash').jump()
      end,
      desc = 'Flash',
    },
    {
      'S',
      mode = { 'n', 'x', 'o' },
      function()
        require('flash').treesitter()
      end,
      desc = 'Flash Treesitter',
    },
    {
      'r',
      mode = 'o',
      function()
        require('flash').remote()
      end,
      desc = 'Remote Flash',
    },
    {
      'R',
      mode = { 'o', 'x' },
      function()
        require('flash').treesitter_search()
      end,
      desc = 'Treesitter Search',
    },
    {
      '<c-s>',
      mode = { 'c' },
      function()
        require('flash').toggle()
      end,
      desc = 'Toggle Flash Search',
    },
  },
}

-- Snacks.nvim - A collection of small QoL plugins for Neovim
-- https://github.com/folke/snacks.nvim

return {
  'folke/snacks.nvim',
  priority = 1000,
  lazy = false,
  opts = {
    -- Enable specific features
    bigfile = { enabled = true }, -- Better performance for large files
    notifier = { enabled = true }, -- Pretty notifications (replaces fidget.nvim)
    quickfile = { enabled = true }, -- Faster file loading
    statuscolumn = { enabled = false }, -- We're using default for now
    words = { enabled = true }, -- Enhanced LSP reference highlighting

    -- Smooth scrolling animations
    scroll = {
      enabled = true,
      animate = {
        duration = { step = 15, total = 150 },
        easing = 'linear',
      },
    },

    -- Better buffer deletion
    bufdelete = {
      enabled = true,
    },

    -- Smart file renaming with LSP integration
    rename = {
      enabled = true,
    },

    -- Configure notifier (replaces fidget.nvim)
    notifier_opts = {
      timeout = 3000,
      width = { min = 40, max = 0.4 },
      height = { min = 1, max = 0.6 },
      margin = { top = 0, right = 1, bottom = 0 },
      icons = {
        error = "󰅚 ",
        warn = "󰀪 ",
        info = "󰋽 ",
        debug = "󰠭 ",
        trace = "󰏫 ",
      },
    },
  },
  keys = {
    -- Notification history
    { '<leader>sn', function() require('snacks').notifier.show_history() end, desc = 'Show notification history' },

    -- Buffer deletion
    { '<leader>bd', function() require('snacks').bufdelete.delete() end, desc = 'Delete buffer' },
    { '<leader>bo', function() require('snacks').bufdelete.other() end, desc = 'Delete other buffers' },

    -- Smart rename
    { '<leader>rn', function() require('snacks').rename.rename_file() end, desc = 'Rename file' },

    -- Dismiss notifications
    { '<leader>nd', function() require('snacks').notifier.hide() end, desc = 'Dismiss all notifications' },
  },
  init = function()
    -- Override vim.notify with snacks notifier
    vim.notify = function(msg, level, opts)
      require('snacks').notifier.notify(msg, level, opts)
    end
  end,
}
